export { default as Headers } from './Headers'

export { headersToString } from './transformers/headersToString'
export { headersToList } from './transformers/headersToList'
export { headersToObject } from './transformers/headersToObject'
export { stringToHeaders } from './transformers/stringToHeaders'
export { listToHeaders } from './transformers/listToHeaders'
export { objectToHeaders } from './transformers/objectToHeaders'

export { reduceHeadersObject } from './transformers/reduceHeadersObject'
export { flattenHeadersList } from './transformers/flattenHeadersList'
export { flattenHeadersObject } from './transformers/flattenHeadersObject'

/* Typings */
export {
  HeadersList,
  FlatHeadersList,
  HeadersObject,
  FlatHeadersObject,
} from './glossary'
const HEADERS_INVALID_CHARACTERS = /[^a-z0-9\-#$%&'*+.^_`|~]/i

export function normalizeHeaderName(name: string): string {
  if (typeof name !== 'string') {
    name = String(name)
  }

  if (HEADERS_INVALID_CHARACTERS.test(name) || name.trim() === '') {
    throw new TypeError('Invalid character in header field name')
  }

  return name.toLowerCase()
}
export function normalizeHeaderValue(value: string): string {
  if (typeof value !== 'string') {
    value = String(value)
  }

  return value
}
import { HeadersList, HeadersObject } from './glossary'
import { normalizeHeaderName } from './utils/normalizeHeaderName'
import { normalizeHeaderValue } from './utils/normalizeHeaderValue'

const NORMALIZED_HEADERS: unique symbol = Symbol('normalizedHeaders')
const RAW_HEADER_NAMES: unique symbol = Symbol('rawHeaderNames')

export default class HeadersPolyfill {
  // Normalized header {"name":"a, b"} storage.
  private [NORMALIZED_HEADERS]: Record<string, string> = {}

  // Keeps the mapping between the raw header name
  // and the normalized header name to ease the lookup.
  private [RAW_HEADER_NAMES]: Map<string, string> = new Map()

  constructor(init?: HeadersInit | HeadersObject | HeadersList) {
    /**
     * @note Cannot check if the `init` is an instance of the `Headers`
     * because that class is only defined in the browser.
     */
    if (
      ['Headers', 'HeadersPolyfill'].includes(init?.constructor.name) ||
      init instanceof HeadersPolyfill
    ) {
      const initialHeaders = init as Headers
      initialHeaders.forEach((value, name) => {
        this.append(name, value)
      }, this)
    } else if (Array.isArray(init)) {
      init.forEach(([name, value]) => {
        this.append(name, Array.isArray(value) ? value.join(', ') : value)
      })
    } else if (init) {
      Object.getOwnPropertyNames(init).forEach((name) => {
        const value = init[name]
        this.append(name, Array.isArray(value) ? value.join(', ') : value)
      })
    }
  }

  [Symbol.iterator]() {
    return this.entries()
  }

  *keys(): IterableIterator<string> {
    for (const name of Object.keys(this[NORMALIZED_HEADERS])) {
      yield name
    }
  }

  *values(): IterableIterator<string> {
    for (const value of Object.values(this[NORMALIZED_HEADERS])) {
      yield value
    }
  }

  *entries(): IterableIterator<[string, string]> {
    for (const name of Object.keys(this[NORMALIZED_HEADERS])) {
      yield [name, this.get(name)]
    }
  }

  /**
   * Returns a `ByteString` sequence of all the values of a header with a given name.
   */
  get(name: string): string | null {
    return this[NORMALIZED_HEADERS][normalizeHeaderName(name)] || null
  }

  /**
   * Sets a new value for an existing header inside a `Headers` object, or adds the header if it does not already exist.
   */
  set(name: string, value: string): void {
    const normalizedName = normalizeHeaderName(name)
    this[NORMALIZED_HEADERS][normalizedName] = normalizeHeaderValue(value)
    this[RAW_HEADER_NAMES].set(normalizedName, name)
  }

  /**
   * Appends a new value onto an existing header inside a `Headers` object, or adds the header if it does not already exist.
   */
  append(name: string, value: string): void {
    const normalizedName = normalizeHeaderName(name)
    let resolvedValue = this.has(normalizedName)
      ? `${this.get(normalizedName)}, ${value}`
      : value

    this.set(name, resolvedValue)
  }

  /**
   * Deletes a header from the `Headers` object.
   */
  delete(name: string): void {
    if (!this.has(name)) {
      return
    }

    const normalizedName = normalizeHeaderName(name)
    delete this[NORMALIZED_HEADERS][normalizedName]
    this[RAW_HEADER_NAMES].delete(normalizedName)
  }

  /**
   * Returns the object of all the normalized headers.
   */
  all(): Record<string, string> {
    return this[NORMALIZED_HEADERS]
  }

  /**
   * Returns the object of all the raw headers.
   */
  raw(): Record<string, string> {
    const rawHeaders: Record<string, string> = {}

    for (const [name, value] of this.entries()) {
      rawHeaders[this[RAW_HEADER_NAMES].get(name)] = value
    }

    return rawHeaders
  }

  /**
   * Returns a boolean stating whether a `Headers` object contains a certain header.
   */
  has(name: string): boolean {
    return this[NORMALIZED_HEADERS].hasOwnProperty(normalizeHeaderName(name))
  }

  /**
   * Traverses the `Headers` object,
   * calling the given callback for each header.
   */
  forEach<ThisArg = this>(
    callback: (
      this: ThisArg,
      value: string,
      name: string,
      parent: this
    ) => void,
    thisArg?: ThisArg
  ) {
    for (const name in this[NORMALIZED_HEADERS]) {
      if (this[NORMALIZED_HEADERS].hasOwnProperty(name)) {
        callback.call(thisArg, this[NORMALIZED_HEADERS][name], name, this)
      }
    }
  }
}
import { HeadersList } from '../glossary'

export function headersToList(headers: Headers): HeadersList {
  const headersList: HeadersList = []

  headers.forEach((value, name) => {
    const resolvedValue = value.includes(',')
      ? value.split(',').map((value) => value.trim())
      : value

    headersList.push([name, resolvedValue])
  })

  return headersList
}
import { headersToList } from './headersToList'

/**
 * Converts a given `Headers` instance to its string representation.
 */
export function headersToString(headers: Headers): string {
  const list = headersToList(headers)
  const lines = list.map(([name, value]) => {
    const values = ([] as string[]).concat(value)
    return `${name}: ${values.join(', ')}`
  })

  return lines.join('\r\n')
}
import { HeadersObject } from '../glossary'

// List of headers that cannot have multiple values,
// while potentially having a comma in their single value.
const singleValueHeaders = ['user-agent']

/**
 * Converts a given `Headers` instance into a plain object.
 * Respects headers with multiple values.
 */
export function headersToObject(headers: Headers): HeadersObject {
  const headersObject: HeadersObject = {}

  headers.forEach((value, name) => {
    const isMultiValue =
      !singleValueHeaders.includes(name.toLowerCase()) && value.includes(',')
    headersObject[name] = isMultiValue
      ? value.split(',').map((s) => s.trim())
      : value
  })

  return headersObject
}
import HeadersPolyfill from '../Headers'

/**
 * Converts a string representation of headers (i.e. from XMLHttpRequest)
 * to a new `Headers` instance.
 */
export function stringToHeaders(str: string): HeadersPolyfill {
  const lines = str.trim().split(/[\r\n]+/)

  return lines.reduce((headers, line) => {
    if (line.trim() === '') {
      return headers
    }

    const parts = line.split(': ')
    const name = parts.shift()
    const value = parts.join(': ')
    headers.append(name, value)

    return headers
  }, new HeadersPolyfill())
}
import HeadersPolyfill from '../Headers'
import { HeadersList } from '../glossary'

export function listToHeaders(list: HeadersList): HeadersPolyfill {
  const headers = new HeadersPolyfill()

  list.forEach(([name, value]) => {
    const values = ([] as string[]).concat(value)

    values.forEach((value) => {
      headers.append(name, value)
    })
  })

  return headers
}
import { HeadersObject } from '../glossary'

/**
 * Reduces given headers object instnace.
 */
export function reduceHeadersObject<R>(
  headers: HeadersObject,
  reducer: (headers: R, name: string, value: string | string[]) => R,
  initialState: R
): R {
  return Object.keys(headers).reduce<R>((nextHeaders, name) => {
    return reducer(nextHeaders, name, headers[name])
  }, initialState)
}
import HeadersPolyfill from '../Headers'
import { reduceHeadersObject } from './reduceHeadersObject'

/**
 * Converts a given headers object to a new `Headers` instance.
 */
export function objectToHeaders(
  headersObject: Record<string, string | string[] | undefined>
): HeadersPolyfill {
  return reduceHeadersObject(
    headersObject,
    (headers, name, value) => {
      const values = ([] as string[]).concat(value).filter(Boolean)

      values.forEach((value) => {
        headers.append(name, value)
      })

      return headers
    },
    new HeadersPolyfill()
  )
}
import { HeadersList, FlatHeadersList } from '../glossary'

export function flattenHeadersList(list: HeadersList): FlatHeadersList {
  return list.map(([name, values]) => {
    return [name, ([] as string[]).concat(values).join(', ')]
  })
}
import { HeadersObject, FlatHeadersObject } from '../glossary'
import { reduceHeadersObject } from './reduceHeadersObject'

export function flattenHeadersObject(
  headersObject: HeadersObject
): FlatHeadersObject {
  return reduceHeadersObject<FlatHeadersObject>(
    headersObject,
    (headers, name, value) => {
      headers[name] = ([] as string[]).concat(value).join(', ')
      return headers
    },
    {}
  )
}
